import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../layout/Header';
import Footer from '../layout/Footer';
import { Container, Row, Col, Spinner, Alert, Card } from 'react-bootstrap';
import { anuncioService } from '../../services/api';

const CategoryPage = () => {
  const { categoriaId } = useParams();
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [categoriaInfo, setCategoriaInfo] = useState(null);

  useEffect(() => {
    const fetchCategoriaInfo = async () => {
      try {
        const categorias = await anuncioService.getCategorias();
        const categoria = categorias.find(cat => cat.ID_Categoria === parseInt(categoriaId));
        setCategoriaInfo(categoria);
      } catch (err) {
        setCategoriaInfo(null);
      }
    };
    fetchCategoriaInfo();
  }, [categoriaId]);

  useEffect(() => {
    const fetchProdutos = async () => {
      setLoading(true);
      setError(null);
      try {
        const produtosFiltrados = await anuncioService.getAnunciosByCategoria(categoriaId);
        setProdutos(produtosFiltrados || []);
      } catch (err) {
        setError('Erro ao carregar anúncios da categoria.');
      } finally {
        setLoading(false);
      }
    };
    fetchProdutos();
  }, [categoriaId]);

  return (
    <>
      <Header />
      <Container className="my-5">
        <h2 className="mb-4">
          {categoriaInfo ? `Anúncios da categoria: ${categoriaInfo.Descricao_Categoria}` : 'Anúncios da Categoria'}
        </h2>
        {loading && <Spinner animation="border" />}
        {error && <Alert variant="danger">{error}</Alert>}
        <Row>
          {produtos.length === 0 && !loading && !error && (
            <Col><Alert variant="info">Nenhum anúncio encontrado para esta categoria.</Alert></Col>
          )}
          {produtos.map(anuncio => (
            <Col key={anuncio.ID_Item} xs={12} md={4} lg={3} className="mb-4">
              <Card className="product-card h-100 shadow-sm">
                <Card.Img variant="top" src={anuncio.imagem_url || '/default-image.jpg'} alt={anuncio.nome} />
                <Card.Body>
                  <Card.Title>{anuncio.nome}</Card.Title>
                  <Card.Text>{anuncio.descricao}</Card.Text>
                  <Card.Text><strong>Preço:</strong> {anuncio.preco ? `R$ ${anuncio.preco}` : 'Consultar'}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
      <Footer />
    </>
  );
};

export default CategoryPage;
