import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../layout/Header';
import Footer from '../layout/Footer';
import { Container, Row, Col, Spinner, Alert } from 'react-bootstrap';
import { anuncioService } from '../../services/api';

// Você pode customizar ou importar um componente de exibição de anúncios, se houver
// Aqui, usarei um Card simples para cada anúncio

const CategoryPage = () => {
  const { id } = useParams();
  const [anuncios, setAnuncios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAnuncios = async () => {
      setLoading(true);
      setError(null);
      try {
        // Supondo que existe um método para buscar anúncios por categoria
        const anunciosCategoria = await anuncioService.getAnunciosByCategoria(id);
        setAnuncios(anunciosCategoria || []);
      } catch (err) {
        setError('Erro ao carregar anúncios da categoria.');
      } finally {
        setLoading(false);
      }
    };
    fetchAnuncios();
  }, [id]);

  return (
    <>
      <Header />
      <Container className="my-5">
        <h2 className="mb-4">Anúncios da Categoria</h2>
        {loading && <Spinner animation="border" />}
        {error && <Alert variant="danger">{error}</Alert>}
        <Row>
          {anuncios.length === 0 && !loading && !error && (
            <Col><Alert variant="info">Nenhum anúncio encontrado para esta categoria.</Alert></Col>
          )}
          {anuncios.map(anuncio => (
            <Col key={anuncio.ID_Item} xs={12} md={4} lg={3} className="mb-4">
              <div className="product-card h-100 shadow-sm">
                <img src={anuncio.imagem_url || '/default-image.jpg'} alt={anuncio.nome} className="img-fluid mb-2" />
                <h5>{anuncio.nome}</h5>
                <p>{anuncio.descricao}</p>
                <p><strong>Preço:</strong> {anuncio.preco ? `R$ ${anuncio.preco}` : 'Consultar'}</p>
              </div>
            </Col>
          ))}
        </Row>
      </Container>
      <Footer />
    </>
  );
};

export default CategoryPage;
